import React from 'react';
import ReactDOM from 'react-dom/client';
import { createNetworkConfig, SuiClientProvider, WalletProvider } from '@mysten/dapp-kit';
import { getFullnodeUrl } from '@mysten/sui/client';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';

// Đảm bảo import CSS để nút ConnectButton hiển thị đúng style
import '@mysten/dapp-kit/dist/index.css'; 
import App from './App';

// 1. Khởi tạo cấu hình mạng (Network Configuration)
const { networkConfig } = createNetworkConfig({
  // Bạn có thể thêm mainnet hoặc devnet ở đây nếu cần sau này
  testnet: { url: getFullnodeUrl('testnet') },
});

// 2. Tạo một QueryClient instance mới
const queryClient = new QueryClient();

// 3. Render ứng dụng
const rootElement = document.getElementById('root');

if (!rootElement) {
  throw new Error("Không tìm thấy phần tử root trong index.html");
}

const root = ReactDOM.createRoot(rootElement);

root.render(
  <React.StrictMode>
    <QueryClientProvider client={queryClient}>
      <SuiClientProvider networks={networkConfig} defaultNetwork="testnet">
        {/* autoConnect sẽ tự động kết nối lại ví nếu người dùng đã dùng trước đó */}
        <WalletProvider autoConnect>
          <App />
        </WalletProvider>
      </SuiClientProvider>
    </QueryClientProvider>
  </React.StrictMode>
);
