import React, { useState } from 'react';
import { useWalletConnection, useTransactionExecution } from '../hooks/useSui';
import { donateTx } from '../utils/suiTransactions';
import './DonateModal.css';

interface DonateModalProps {
  campaign: {
    id: string;
    title: string;
    current_amount: number;
    goal_amount: number;
  };
  onClose: () => void;
  onSuccess?: () => void;
}

const DonateModal: React.FC<DonateModalProps> = ({ campaign, onClose, onSuccess }) => {
  const { account, isConnected, connect } = useWalletConnection();
  const { executeTransaction } = useTransactionExecution();

  const [amount, setAmount] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!isConnected) {
      connect();
      return;
    }

    if (!amount || parseFloat(amount) <= 0) {
      setError('Vui lÃ²ng nháº­p sá»‘ tiá»n há»£p lá»‡');
      return;
    }

    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // Convert donation amount from SUI to MIST (1 SUI = 10^9 MIST)
      const amountMIST = BigInt(amount) * BigInt(10 ** 9);

      // Sui Clock object ID
      const clockId = '0x6';

      // TODO: Get user's coin object ID from wallet
      const coinObjectId = '0xcoinid'; // Placeholder

      const tx = await donateTx(
        campaign.id,
        coinObjectId,
        Number(amountMIST),
        clockId
      );

      const result = await executeTransaction(tx);

      setSuccess(`âœ… QuyÃªn gÃ³p thÃ nh cÃ´ng! TX: ${(result as any).digest}`);
      setAmount('');

      // Call success callback after 2 seconds
      setTimeout(() => {
        onSuccess?.();
        onClose();
      }, 2000);

    } catch (err: any) {
      setError(`âŒ Lá»—i: ${err.message || 'KhÃ´ng thá»ƒ quyÃªn gÃ³p'}`);
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const quickAmounts = [1, 5, 10, 50];

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>ğŸ’ QuyÃªn GÃ³p Cho Chiáº¿n Dá»‹ch</h3>
          <button className="close-btn" onClick={onClose}>âœ•</button>
        </div>

        <div className="modal-body">
          <div className="campaign-info">
            <h4>{campaign.title}</h4>
            <p className="progress-info">
              {campaign.current_amount.toLocaleString()} / {campaign.goal_amount.toLocaleString()} SUI
            </p>
            <div className="progress-bar">
              <div 
                className="progress-fill" 
                style={{
                  width: `${Math.min(
                    (campaign.current_amount / campaign.goal_amount) * 100,
                    100
                  )}%`
                }}
              ></div>
            </div>
          </div>

          {!isConnected && (
            <div className="warning-box">
              <p>âš ï¸ Vui lÃ²ng káº¿t ná»‘i vÃ­ Ä‘á»ƒ quyÃªn gÃ³p</p>
            </div>
          )}

          {error && <div className="error-box">{error}</div>}
          {success && <div className="success-box">{success}</div>}

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="amount">ğŸ’° Sá»‘ Tiá»n (SUI)</label>
              <input
                id="amount"
                type="number"
                step="0.01"
                min="0.01"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                placeholder="Nháº­p sá»‘ tiá»n"
                disabled={!isConnected || loading}
                required
                autoFocus
              />
            </div>

            <div className="quick-amounts">
              <label>Hoáº·c chá»n nhanh:</label>
              <div className="quick-buttons">
                {quickAmounts.map((quick) => (
                  <button
                    key={quick}
                    type="button"
                    className="quick-btn"
                    onClick={() => setAmount(quick.toString())}
                    disabled={!isConnected || loading}
                  >
                    {quick} SUI
                  </button>
                ))}
              </div>
            </div>

            <button
              type="submit"
              className="btn-donate"
              disabled={!isConnected || loading || !amount}
            >
              {loading ? 'â³ Äang xá»­ lÃ½...' : `ğŸ’ QuyÃªn GÃ³p ${amount ? amount : ''} SUI`}
            </button>
          </form>
        </div>

        <div className="modal-footer">
          <button className="btn-close" onClick={onClose}>ÄÃ³ng</button>
        </div>
      </div>
    </div>
  );
};

export default DonateModal;