export interface Campaign {
  id: number;
  title: string;
  description: string;
  image: string;
  raised: number;
  goal: number;
  donors: number;
  category: string;
}

export class CampaignManager {
  private campaigns: Campaign[];
  constructor(campaigns: Campaign[]) { this.campaigns = campaigns; }
  getCampaigns() { return this.campaigns; }
  getProgress(c: Campaign) { return Math.min((c.raised / c.goal) * 100, 100); }
  getTotalRaised() { return this.campaigns.reduce((sum, c) => sum + c.raised, 0); }
}

